import React, { useState } from "react";
import dummyData from "../static/dummyData";

const Filterbar = () => {
  //   const parsedDate = new Date(dummyData[0].createdAt).toLocaleDateString("ko-kr");
  //   console.log("@@@@@", parsedDate);
  const [datas, setDatas] = useState(dummyData);
  //   console.log("!!!!!!!", datas);
  const dDay = dummyData
    .map((el) => {
      return el.d_day;
    })
    .sort((a, b) => a - b);
  //   console.log("dDay~", dDay);
  const handleChange = () => {
    console.log("dDay~", dDay);
  };
  const today = new Date();

  return (
    <div>
      <div className="menu">
        <select>
          드롭다운
          <option>--정렬방식 선택--</option>
          <option
            value=""
            onChange={() => {
              handleChange();
            }}
          >
            D-day순
          </option>
          <option>생성날짜순</option>
        </select>
      </div>
      <p>
        {today.getFullYear()}년{today.getMonth() + 1}월{today.getDate()}일
      </p>
      <div>
        {datas.map((el) => {
          return <p key={el.id}>{el.content}</p>;
        })}
      </div>
    </div>
  );
};

export default Filterbar;
