import React, { useState } from "react";
import dummyData from "../static/dummyData";

const Filterbar = () => {
  //   const parsedDate = new Date(dummyData.createdAt).toLocaleDateString("ko-kr");
  //   console.log("@@@", parsedDate);
  const [content, setContent] = useState(dummyData.map((el) => el.content));
  const dDay = dummyData
    .map((el) => {
      return el.d_day;
    })
    .sort((a, b) => a - b);
  console.log("dDay~", dDay);
  const handleChange = () => {
    alert();
  };
  const today = new Date();
  return (
    <div>
      <div className="menu">
        <select>
          드롭다운
          <option>--정렬방식 선택--</option>
          <option value="">D-day순</option>
          <option value="">날짜순</option>
          <option
            onChange={() => {
              handleChange();
            }}
          >
            생성날짜순
          </option>
        </select>
      </div>
      <div>
        {today.getFullYear()}년{today.getMonth() + 1}월{today.getDate()}일
      </div>
      <div>
        {dummyData.map((el) => {
          return <p key={el.id}>{el.content}</p>;
        })}
      </div>
    </div>
  );
};

export default Filterbar;
